// EditRecipe.vue

<script>
import api from '../apiService';

export default {
  data() {
    return {
      recipe: {
        title: '',
        ingredients: '',
        instructions: '',
        image: null  // Add an image field to bind with input[type="file"]
      }
    };
  },
  created() {
    this.fetchRecipe();
  },
  methods: {
    fetchRecipe() {
      const recipeId = this.$route.params.id;
      api.get(`recipes/${recipeId}/`)
        .then(response => {
          this.recipe = response.data;
        })
        .catch(error => {
          console.error('Error fetching recipe:', error);
        });
    },
    updateRecipe() {
      const recipeId = this.$route.params.id;
      let formData = new FormData();
      formData.append('title', this.recipe.title);
      formData.append('ingredients', this.recipe.ingredients);
      formData.append('instructions', this.recipe.instructions);
      formData.append('image', this.recipe.image); // Append the image file

      api.put(`recipes/${recipeId}/`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      })
        .then(response => {
          console.log('Recipe updated successfully:', response);
          // Optionally, navigate back to recipe list or stay on this page
        })
        .catch(error => {
          console.error('Error updating recipe:', error);
        });
    },
    handleFileUpload(event) {
      this.recipe.image = event.target.files[0]; // Capture selected file
    }
  }
};
</script>

<template>
  <div>
    <h2>Edit Recipe</h2>
    <form @submit.prevent="updateRecipe">
      <div>
        <label for="title">Title</label>
        <input type="text" id="title" v-model="recipe.title" required>
      </div>
      <div>
        <label for="ingredients">Ingredients</label>
        <textarea id="ingredients" v-model="recipe.ingredients" required></textarea>
      </div>
      <div>
        <label for="instructions">Instructions</label>
        <textarea id="instructions" v-model="recipe.instructions" required></textarea>
      </div>
      <div>
        <label for="image">Image</label>
        <input type="file" id="image" @change="handleFileUpload" accept="image/*">
      </div>
      <button type="submit">Update Recipe</button>
    </form>
  </div>
</template>

<style>
/* Add custom styles for form and inputs as needed */
form {
  max-width: 600px;
  margin: auto;
  padding: 1em;
  border: 1px solid #ccc;
  border-radius: 5px;
}

label {
  font-weight: bold;
  margin-bottom: 0.5em;
}

input[type="text"],
textarea {
  width: 100%;
  padding: 0.5em;
  margin-bottom: 1em;
  border: 1px solid #ccc;
  border-radius: 3px;
}

input[type="file"] {
  margin-top: 0.5em;
}

button[type="submit"] {
  padding: 0.5em 1em;
  background-color: #007BFF;
  color: white;
  border: none;
  border-radius: 5px;
  cursor: pointer;
}

button[type="submit"]:hover {
  background-color: #0056b3;
}

button[type="submit"]:active {
  background-color: #00408d;
}
</style>
